mapper.update:
UPDATE  ORDERS
SET    	ORDERS_STATUS = '거래완료'
WHERE   ORDERS_PRONUM = #{ORDERS_NUM}

mapper.select:
SELECT
ROW_NUMBER() OVER (ORDER BY G.GOODS_NUM DESC) RNUM, G.GOODS_NUM, G.GOODS_TITLE, G.GOODS_PRICE, (select MEM_ID FROM MEMBER WHERE MEM_NUM= G.GOODS_SELLER) SELLER_ID, G.GOODS_REGION, G.GOODS_CATEGORY, G.GOODS_COUNT, G.GOODS_DATE, TO_CHAR(G.GOODS_DATE,'hh24:mi:ss') AS GOODS_TIME, G.GOODS_STATUS, G.GOODS_THUMBNAIL, G.GOODS_TSTATUS, G.GOODS_CONTENT
FROM GOODS G, ( SELECT MEM_NUM, MEM_ID FROM MEMBER ) M, ( SELECT distinct GOODS_LIKE_PARENT, GOODS_LIKE_USER FROM GOODS_LIKE
) GL
WHERE GL.GOODS_LIKE_USER = M.MEM_NUM(+) AND G.GOODS_NUM = GL.GOODS_LIKE_PARENT(+) AND G.GOODS_STATUS = 'N' AND G.GOODS_TSTATUS IN ('N', 'ING', 'END')
AND M.MEM_ID = #{MEM_ID}

mapper.update:
	UPDATE  ORDERS
	SET     ORDERS_PHONE = #{ORDERS_PHONE},
  	        ORDERS_DADD1 = #{ORDERS_DADD1},
  	        ORDERS_DADD2  =  #{ORDERS_DADD2},
            ORDERS_DMEMO =  #{ORDERS_DMEMO}


	WHERE   ORDERS_NUM = #{ORDERS_NUM}

mapper.select:
SELECT  GOODS_SELLER
FROM    GOODS
WHERE   GOODS_NUM = #{ORDERS_PRONUM}
